   {
        path: '/role',
        name: 'role',
        component: RoleManage
    }
    /*,
    {
        path: '/menu',
        name: 'menu',
        component: MenuManage
    }*/
]

const router = new VueRouter({
    mode: 'hash',
    base: process.env.BASE_URL,
    routes
});


export default router
